graphql.query:
mutation login ($email: String!, $password: String!) {
    login (email: $email, password: $password) {
        email
        firstName
        id
        lastName
        name
    }
}

mutation register ($data: RegisterInput!) {
    register (data: $data) {
        email
        firstName
        id
        lastName
        name
    }
}

query me {
    me {
        email
        firstName
        id
        lastName
        name
    }
}
